mapper.insert:
INSERT INTO PROJECT
( NAME
, LOGO_NAME
, LOGO_PATH
, SUMMARY
, ATTENDANT_NUMBER
, START_DATE
, END_DATE
, CREATE_DATE
, MODIFY_DATE)
VALUES ( #{name}
       , #{logoName}
       , #{logoPath}
       , #{summary}
       , #{attendantNumber}
       , #{startDate}
       , #{endDate}
       , NOW()
       , NOW())

mapper.update:
UPDATE PROJECT
MODIFY_DATE = NOW(), NAME = #{name}, LOGO_NAME = #{logoName}, LOGO_PATH = #{logoPath}, SUMMARY = #{summary}, START_DATE = #{startDate}, END_DATE = #{endDate},
WHERE ID = #{id}

UPDATE PROJECT_ATTENDANT
SET JOIN_DATE = NOW()
WHERE PROFILEID = #{profileId}
AND PROJECTID = #{id}
