mapper.insert:
INSERT INTO PROJECT
( NAME
, LOGO_NAME
, LOGO_PATH
, SUMMARY
, ATTENDANT_NUMBER
, CREATE_DATE)
VALUES ( #{name}
       , #{logoName}
       , #{logoPath}
       , #{summary}
       , #{attendantNumber}
       , NOW())

mapper.update:
UPDATE PROJECT
MODIFY_DATE = NOW(), NAME = #{name}, LOGO_NAME = #{logoName}, LOGO_PATH = #{logoPath}, SUMMARY = #{summary}, START_DATE = #{startDate}, END_DATE = #{endDate},
WHERE ID = #{id}

mapper.insert:
INSERT INTO PROJECT_ATTENDANT
(
 PROFILEID,
 PROJECTID,
 STATUS,
 INVITE_DATE
)
VALUES (
        #{profileId}
       , #{id}
       , 'R'
       , NOW()
       )

mapper.update:
UPDATE PROJECT_ATTENDANT
SET JOIN_DATE = NOW(),
    STATUS = 'Y'
WHERE PROFILEID = #{profileId}
AND PROJECTID = #{id}

UPDATE PROJECT_ATTENDANT
SET JOIN_DATE = NOW(),
    STATUS = 'N'
WHERE PROFILEID = #{profileId}
  AND PROJECTID = #{id}

mapper.insert:
INSERT INTO TEAM
(
 PROJECT_ID,
 NAME
)
VALUES (
        #{id},
        #{name}
       )

mapper.update:
UPDATE PROJECT_ATTENDANT
SET team_id = #{teamId},
WHERE PROFILEID = #{profileId}
  AND PROJECTID = #{id}

mapper.select:
SELECT pt2.* FROM PROJECT_TOOL pt2, PROJECT p
WHERE pt2.project_id = p.ID
  AND p.ID = #{projectId}
